import React, { Component } from 'react';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchTerm: ''
        };
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick() {
        alert('hello')
    }
    render() {
        return (
            <div>
                {/* Display JSON data below */}
                <input type="text" placeholder="Search" className="search" onClick={this.onClick}></input>
                {this.state.searchTerm}
            </div>
        )
    }
};

export default Search;
