import React, { Component } from 'react';
import MainClass from '../classes/MainClass';
import data from '../../assets/data.json';


class FilterClass extends Component {
    constructor(props) {
        super(props);
        this.state = {
            applyTeacher  : false,
            applyDuration : false,
            applyLevel    : false,
            applyStyle    : false,
            applyAnatomy  : false
        }
    }
    onClick() {
        let duration;
        if (duration === 5) {
        this.setState({applyDuration: true});
            // Update JSON data here 
        }
        // duration === 5 ? do something : do something; duration === 10 ? do something : do something;
    }
    render() {
        let filteredArray = [];
        const filter = data.map((element) => {
           return filteredArray = {
               "teacher" : element["teacher"],
               "duration": element["Duration"], 
               "level"   : element["Level"],
               "style"   : element["Style"],
               "anatomy" : element["Anatomy"],
            }
        });
        return (
            <div>
                <p>Filtered Data: {filteredArray}</p>
            </div>
        )
    }
};

export default FilterClass;
