import React, { Component } from 'react';

class MainClass extends Component { 
  constructor() {
    super();
    this.state = {
        data: ''
    }
  }
  componentDidMount() {
    fetch('../../assets/data.json')
      .then(response => response.json())
      .then(data => this.setState({ data }));
  }
  render() {
      return (
          <div><p>{this.state.data}</p></div>
      )
  }
}
export default MainClass;
